// Очистка вывода от Chain-of-Thought (умная фильтрация по языкам)
let text = $input.item.json.output;

if (text && typeof text === 'string') {
  const lines = text.split('\n');
  let cleanLines = [];

  for (let line of lines) {
      const trimmed = line.trim();
      if (trimmed.length === 0) {
          // Сохраняем пустые строки, но только если уже начали собирать контент
          if (cleanLines.length > 0) cleanLines.push(line);
          continue;
      }

      // Считаем русские и английские буквы
      const ruCount = (trimmed.match(/[а-яА-ЯёЁ]/g) || []).length;
      const enCount = (trimmed.match(/[a-zA-Z]/g) || []).length;

      // Эвристика фильтрации:
      // 1. Строка в основном на русском (ru >= en) -> ОСТАВЛЯЕМ
      if (ruCount >= enCount) {
          cleanLines.push(line);
      } 
      // 2. Строка чисто на английском (много en, 0 ru) -> УДАЛЯЕМ
      else if (enCount > 3 && ruCount === 0) {
           continue; 
      } 
      // 3. Смесь, но английского сильно больше (en > ru * 1.5) -> УДАЛЯЕМ
      // (Пример: "Let's check snippet: <русская цитата>")
      else if (enCount > ruCount * 1.5) { 
           continue;
      }
      // 4. Мало текста (цифры, знаки) или сложный случай -> ОСТАВЛЯЕМ
      else {
           cleanLines.push(line);
      }
  }

  text = cleanLines.join('\n').trim();
}

return { json: { output: text } };
